using namespace std;
using namespace lcd;

// Description of |2551. Put Marbles in Bags|:
//
// You have |k| bags. You are given a 0-indexed integer array |weights| where
// |weights[i]| is the weight of the |iᵗʰ| marble. You are also given the
// integer |k.| Divide the marbles into the |k| bags according to the following
// rules:
//
//  • No bag is empty.
//
//  • If the |iᵗʰ| marble and |jᵗʰ| marble are in a bag, then all marbles with
//  an index between the |iᵗʰ| and |jᵗʰ| indices should also be in that same
//  bag.
//
//  • If a bag consists of all the marbles with an index from |i| to |j|
//  inclusively, then the cost of the bag is |weights[i] + weights[j]|.
// The score after distributing the marbles is the sum of the costs of all the
// |k| bags. Return the difference between the maximum and minimum scores among
// marble distributions.
//
//

LEETCODE_BEGIN_RESOLVING(2551, PutMarblesInBags, Solution);

class Solution {
public:
  long long putMarbles(vector<int> &weights, int k) {
    vector<int64_t> sums;
    for (int i = 1; i < weights.size(); ++i) {
      sums.emplace_back((int64_t)weights[i] + (int64_t)weights[i - 1]);
    }
    sort(sums.begin(), sums.end());
    return accumulate(sums.rbegin(), sums.rbegin() + (k - 1), 0l) -
           accumulate(sums.begin(), sums.begin() + (k - 1), 0l);
  }
};

LEETCODE_END_RESOLVING(Solution);

